class CryptoUtils:
    @staticmethod
    def generate_x25519_keypair():
        private_key = x25519.X25519PrivateKey.generate()
        public_key = private_key.public_key()
        return private_key, public_key
    
    @staticmethod
    def generate_ed25519_keypair():
        private_key = ed25519.Ed25519PrivateKey.generate()
        public_key = private_key.public_key()
        return private_key, public_key
    
    @staticmethod
    def x25519_shared_secret(private_key, peer_public_key):
        return private_key.exchange(peer_public_key)
    
    @staticmethod
    def derive_aes_key(shared_secret):
        salt = b'\x00' * 16
        hkdf = HKDF(
            algorithm=hashes.SHA256(),
            length=32,
            salt=salt,
            info=b'x25519 key derivation',
            backend=default_backend()
        )
        return hkdf.derive(shared_secret)
    
    @staticmethod
    def encrypt_message(key, message):
        # 添加PKCS7填充
        padder = padding.PKCS7(128).padder()
        padded_data = padder.update(message) + padder.finalize()
        
        iv = os.urandom(16)
        cipher = Cipher(algorithms.AES(key), modes.CBC(iv), backend=default_backend())
        encryptor = cipher.encryptor()
        ciphertext = encryptor.update(padded_data) + encryptor.finalize()
        
        return iv + ciphertext
    
    @staticmethod
    def decrypt_message(key, encrypted_data):
        iv = encrypted_data[:16]
        ciphertext = encrypted_data[16:]
        
        cipher = Cipher(algorithms.AES(key), modes.CBC(iv), backend=default_backend())
        decryptor = cipher.decryptor()
        padded_plaintext = decryptor.update(ciphertext) + decryptor.finalize()
        
        # 移除PKCS7填充
        unpadder = padding.PKCS7(128).unpadder()
        try:
            plaintext = unpadder.update(padded_plaintext) + unpadder.finalize()
        except ValueError:
            # 处理填充错误
            print("Padding error, returning original data")
            return padded_plaintext
        
        return plaintext
    
    @staticmethod
    def sign_message(private_key, message):
        return private_key.sign(message)
    
    @staticmethod
    def verify_signature(public_key, message, signature):
        try:
            public_key.verify(signature, message)
            return True
        except:
            return False
    
    @staticmethod
    def public_key_to_bytes(public_key):
        return public_key.public_bytes(
            encoding=serialization.Encoding.Raw,
            format=serialization.PublicFormat.Raw
        )
    
    @staticmethod
    def bytes_to_x25519_public_key(public_bytes):
        return x25519.X25519PublicKey.from_public_bytes(public_bytes)
    
    @staticmethod
    def bytes_to_ed25519_public_key(public_bytes):
        return ed25519.Ed25519PublicKey.from_public_bytes(public_bytes)

class APIClient:
    @staticmethod
    async def register(username, password, display_name):
        url = f"{SERVER_URL}/register"
        payload = {
            "username": username,
            "password": password,
            "display_name": display_name
        }
        try:
            async with aiohttp.ClientSession() as session:
                async with session.post(url, json=payload) as response:
                    return await response.json()
        except Exception as e:
            print(f"Registration error: {e}")
            return {"status": "error", "message": "无法连接到服务器"}
    
    @staticmethod
    async def login(login_identifier, password):
        url = f"{SERVER_URL}/login"
        payload = {
            "login_identifier": login_identifier,
            "password": password
        }
        try:
            async with aiohttp.ClientSession() as session:
                async with session.post(url, json=payload) as response:
                    return await response.json()
        except Exception as e:
            print(f"Login error: {e}")
            return {"status": "error", "message": "无法连接到服务器"}
    
    @staticmethod
    async def get_user_public_key(user_id, key_type="x25519"):
        url = f"{SERVER_URL}/get_public_key/{user_id}"
        params = {"key_type": key_type}
        try:
            async with aiohttp.ClientSession() as session:
                async with session.get(url, params=params) as response:
                    return await response.json()
        except Exception as e:
            print(f"Get public key error: {e}")
            return {"status": "error", "message": "无法连接到服务器"}
    
    @staticmethod
    async def upload_public_key(user_id, auth_token, x25519_pub_key, ed25519_pub_key):
        url = f"{SERVER_URL}/upload_public_key"
        payload = {
            "user_id": user_id,
            "auth_token": auth_token,
            "x25519_public_key": base64.b64encode(x25519_pub_key).decode('utf-8'),
            "ed25519_public_key": base64.b64encode(ed25519_pub_key).decode('utf-8')
        }
        try:
            async with aiohttp.ClientSession() as session:
                async with session.post(url, json=payload) as response:
                    return await response.json()
        except Exception as e:
            print(f"Upload public key error: {e}")
            return {"status": "error", "message": "无法连接到服务器"}
    
    @staticmethod
    async def send_message(sender_id, auth_token, recipient_id, encrypted_message, signature):
        url = f"{SERVER_URL}/send_message"
        payload = {
            "sender_id": sender_id,
            "auth_token": auth_token,
            "recipient_id": recipient_id,
            "message": base64.b64encode(encrypted_message).decode('utf-8'),
            "signature": base64.b64encode(signature).decode('utf-8')
        }
        try:
            async with aiohttp.ClientSession() as session:
                async with session.post(url, json=payload) as response:
                    return await response.json()
        except Exception as e:
            print(f"Send message error: {e}")
            return {"status": "error", "message": "无法连接到服务器"}
    
    @staticmethod
    async def get_messages(user_id, auth_token):
        url = f"{SERVER_URL}/get_messages/{user_id}"
        params = {"auth_token": auth_token}
        try:
            async with aiohttp.ClientSession() as session:
                async with session.get(url, params=params) as response:
                    return await response.json()
        except Exception as e:
            print(f"Get messages error: {e}")
            return {"status": "error", "message": "无法连接到服务器"}

