         # 创建组合框用于显示登录历史
        self.user_id_combo = QComboBox()
        self.user_id_combo.setEditable(True)
        self.user_id_combo.setPlaceholderText("输入您的5位用户ID或用户名")
        self.user_id_combo.setMinimumWidth(200)
        self.user_id_combo.setInsertPolicy(QComboBox.InsertAtTop)
        self.user_id_combo.setSizeAdjustPolicy(QComboBox.AdjustToMinimumContentsLengthWithIcon)
        
        # 加载登录历史
        self.load_login_history()
        
        form_layout.addRow("ID/用户名:", self.user_id_combo)
        
